import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/cart.css";

const Cart = () => {
  const [products, setProducts] = useState([]);
  const username = localStorage.getItem("username");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/cart/${username}`);
        setProducts(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProducts();
  }, [username]);

  const handleBuy = (product) => {
    console.log(product);
    axios
      .post("http://localhost:3001/buy", product)
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div className="cart">
      <h2 className="cart-title">My Cart</h2>
      <div className="cart-item">
        {products.map((product) => (
          <div key={product.id}>
            <div className="cart-item-image">
              <img
                src="https://imgs.search.brave.com/b64ISZzpFb5umEzliLXr-JvnQN_OhHmS-znep9LfhRk/rs:fit:1200:810:1/g:ce/aHR0cHM6Ly93d3cu/YWJ1bmRhbnRoZWFs/dGguaW5mby93cC1j/b250ZW50L3VwbG9h/ZHMvMjAyMS8wMS9P/cmdhbmljLXByb2R1/Y2UuanBn"
                alt="Product 1"
              />
            </div>
            <div className="cart-item-details">
              <h3>{product.Name}</h3>
              <p>Price: RS{product.price}</p>
              <p>Description: {product.description}</p>
            </div>
            <div className="cart-item-quantity">
              <input type="number" min="1" value="1" />
              <button>Update</button>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-total">
        <span>Total:</span>
        <strong>$25.00</strong>
      </div>
      <button>Checkout</button>
    </div>
  );
};
export default Cart;
