import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

import axios from "axios";
import "../css/dashboard.css";

const HomePage = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]);
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [cart, setCart] = useState([]);

  const handleAddToCart = (product) => {
    const addToCart = (product) => {
      return {
        type: "ADD_TO_CART",
        payload: product,
      };
    };
  };

  const addToCart = async (product) => {
    const productId = product.id;
    const username = localStorage.getItem("username");
    try {
      const res = await axios.post("http://localhost:3001/cart", {
        product,
        username,
      });
      console.log(res.data.message);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSearch = (event) => {
    event.preventDefault();
    // Perform search
  };

  useEffect(() => {
    axios
      .get("http://localhost:3001/products")
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        setError(error);
      });
  }, []);

  const handleClick = (product) => {
    navigate("/product");
  };

  return (
    <div className="home-page">
      <section className="hero">
        <div className="hero-content">
          <h1>Have a Better LifeStyle!</h1>
          <form className="search-form" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </form>
        </div>
        <img
          src="https://imgs.search.brave.com/L-KnbpsLzaZQTM_ehERn1-tA_qE2gJdv-DL_FS9qhm4/rs:fit:713:225:1/g:ce/aHR0cHM6Ly90c2Ux/Lm1tLmJpbmcubmV0/L3RoP2lkPU9JUC5H/Y1Z5QjgxRVo4VXI5/ZlpsUlBOQmFRSGFF/NyZwaWQ9QXBp"
          alt="Organic Products"
        />
      </section>
      <div className="new">
        <h2>New Products</h2>
      </div>

      <div className="products-section">
        <div className="products-container">
          {products.map((product) => (
            <Link to={`/product/${product.id}`}>
              <div className="product-card" key={product.id}>
                <img
                  src={process.env.PUBLIC_URL + `/images/${product.image}`}
                  alt={product.Name}
                />
                <h3>{product.Name}</h3>
                <p>RS. {product.price}</p>
                <button onClick={() => addToCart(product)}>Add to Cart</button>
              </div>
            </Link>
          ))}
        </div>
      </div>
      <div className="new">
        <h2>Herbs</h2>
      </div>
      <div className="products-section">
        <div className="products-container">
          {products.map((product) => (
            <Link to={`/product/${product.id}`}>
              <div className="product-card" key={product.id}>
                <img
                  src={process.env.PUBLIC_URL + `/images/${product.image}`}
                  alt={product.Name}
                />
                <h3>{product.Name}</h3>
                <p>RS {product.price}</p>
                <button onClick={() => handleAddToCart(product)}>
                  Add to Cart
                </button>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
