import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/cart.css";
import Navbar from "./navbar";
import { BiTrash } from "react-icons/bi";

const Cart = () => {
  const [products, setProducts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [quantities, setQuantities] = useState({});
  const username = localStorage.getItem("username");
  const [email, setEmail] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/cart/${username}`);
        setProducts(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProducts();
  }, [username]);

  const handleQuantityChange = (event, productId) => {
    const newQuantities = { ...quantities };
    newQuantities[productId] = parseInt(event.target.value);
    setQuantities(newQuantities);
  };

  const calculateTotalPrice = () => {
    let totalPrice = 0;
    for (const product of products) {
      const quantity = quantities[product.id] || 1;
      totalPrice += product.price * product.quantity;
    }
    return totalPrice;
  };

  const handlebuy = () => {
    setShowForm(true);
  };

  const handleCheckout = () => {
    const items = [];
    for (const product of products) {
      const username = localStorage.getItem("username");
      const quantity = product.quantity;
      const price = product.price;
      console.log(price);
      items.push({
        id: product.Name,
        quantity,
        username,
        price,
      });
      console.log(quantity);

      console.log(username);
      console.log(product);
    }
    const username = localStorage.getItem("username");
    axios
      .post("http://localhost:3001/order", { items, username })
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
    alert("Purhcase successful");

    window.location.reload();
  };

  const handleBuy = (product) => {
    console.log(product);
    axios
      .post("http://localhost:3001/buy", product)
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <Navbar />
      <div className="cart">
        <h2 className="cart-title">My Cart</h2>
        {products.map((product) => (
          <div className="cart-item" key={product.id}>
            <div className="cart-item-image">
              <img
                src={`data:image/jpeg;base64,${product.pic}`}
                alt={product.Name}
              />
            </div>
            <div className="cart-item-details">
              <h3>{product.Name}</h3>
              <p>Price: RS. {product.price}</p>
            </div>
            <div className="cart-item-quantity">
              <p>{product.quantity}</p>
            </div>
            <BiTrash size={28} color={"#FF0000"} onClick={handleBuy} />
          </div>
        ))}
        <div className="cart-total">
          <span>Total:</span>
          <strong>RS{calculateTotalPrice()}</strong>
        </div>
        <button onClick={handlebuy}>Checkout</button>
        {showForm && (
          <div className="modal">
            <div className="modal-form">
              <input
                type="email"
                name="email"
                placeholder="email"
                id="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <button onClick={() => setShowForm(false)}> BAck</button>
              <button onClick={handleCheckout}> Checkout</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
