import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "./adminside";
import AdminNavbar from "./adminNav";

import "../../css/admin.css";

const AddHealth = () => {
  const [name, setname] = useState("");
  const [solution1, setsolution1] = useState("");
  const [solution2, setsolution2] = useState("");
  const [solution3, setsolution3] = useState("");
  const [solution4, setsolution4] = useState("");
  const [solution5, setsolution5] = useState("");

  // const [selectedFile, setSelectedFile] = useState(null);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handlesubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", name);
    formData.append("solution1", solution1);
    formData.append("solution2", solution2);
    formData.append("solution3", solution3);
    formData.append("solution4", solution4);
    formData.append("solution5", solution5);
    try {
      axios
        .post("http://localhost:3001/addproduct", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        })
        .then((response) => {
          console.log(response);
          if (response.data.message) {
            console.log(response.data.message);
          } else {
            console.log(
              "ACCOUNT CREATED SUCCESSFULLY CHECK YOU EMAIL FOR VERIFICATION "
            );
          }
        });
    } catch (error) {
      if (error.response) {
        console.log(error.response.data.message);
      }
    }
  };

  return (
    <div className="main-content">
      <Sidebar />
      <AdminNavbar />
      <form onSubmit={handlesubmit} method="post" encType="multipart/form-data">
        <h1>Add Health Problem</h1>
        <div className="space"></div>

        <input
          type="text"
          name="name"
          placeholder="Name of Health Problem"
          id="name"
          required
          value={name}
          onChange={(e) => setname(e.target.value)}
        />
        <input
          type="type"
          name="type"
          placeholder="Solution 1"
          id="type"
          required
          value={solution1}
          onChange={(e) => setsolution1(e.target.value)}
        />
        <input
          type="type"
          name="type"
          placeholder="Solution 2"
          id="type"
          required
          value={solution2}
          onChange={(e) => setsolution2(e.target.value)}
        />
        <input
          type="type"
          name="type"
          placeholder="Solution 3"
          id="type"
          required
          value={solution3}
          onChange={(e) => setsolution3(e.target.value)}
        />
        <input
          type="type"
          name="type"
          placeholder="Solution 4"
          id="type"
          required
          value={solution4}
          onChange={(e) => setsolution4(e.target.value)}
        />
        <input
          type="type"
          name="type"
          placeholder="Solution 5"
          id="type"
          required
          value={solution5}
          onChange={(e) => setsolution5(e.target.value)}
        />

        <br />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default AddHealth;
