import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/cart.css";
import Navbar from "./navbar";

const Cart = () => {
  const [products, setProducts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [quantities, setQuantities] = useState({});
  const username = localStorage.getItem("username");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/cart/${username}`);
        setProducts(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProducts();
  }, [username]);

  const handleQuantityChange = (event, productId) => {
    const newQuantities = { ...quantities };
    newQuantities[productId] = parseInt(event.target.value);
    setQuantities(newQuantities);
  };

  const calculateTotalPrice = () => {
    let totalPrice = 0;
    for (const product of products) {
      const quantity = quantities[product.id] || 1;
      totalPrice += product.price * product.quantity;
    }
    return totalPrice;
  };

  const handlebuy = () => {
    setShowForm(true);
  };

  const handleCheckout = () => {
    const items = [];
    for (const product of products) {
      const username = localStorage.getItem("username");
      const quantity = product.quantity;
      const price = product.price;
      console.log(price);
      items.push({
        id: product.Name,
        quantity,
        username,
        price,
      });
      console.log(quantity);

      console.log(username);
      console.log(product);
    }
    const username = localStorage.getItem("username");
    axios
      .post("http://localhost:3001/order", { items, username })
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
    alert("Purhcase successful");
    window.location.reload();
  };

  const handleBuy = (product) => {
    console.log(product);
    axios
      .post("http://localhost:3001/buy", product)
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <Navbar />
      <div className="cart">
        <h2 className="cart-title">My Cart</h2>
        {products.map((product) => (
          <div className="cart-item" key={product.id}>
            <div className="cart-item-image">
              <img
                src={`data:image/jpeg;base64,${product.pic}`}
                alt={product.Name}
              />
            </div>
            <div className="cart-item-details">
              <h3>{product.Name}</h3>
              <p>Price: RS. {product.price}</p>
            </div>
            <div className="cart-item-quantity">
              <p>{product.quantity}</p>
            </div>
          </div>
        ))}

        <div className="cart-total">
          <span>Total:</span>
          <strong>RS{calculateTotalPrice()}</strong>
        </div>
        <button onClick={handlebuy}>Checkout</button>
        {showForm && (
          <div className="modal">
            <div className="modal-form">
              <div className="row">
                <div className="col-75">
                  <div className="container">
                    <form action="/action_page.php">
                      <div className="row">
                        <div className="col-50">
                          <h3>Billing Address</h3>
                          <label htmlFor="fname">
                            <i className="fa fa-user" /> Full Name
                          </label>
                          <input
                            type="text"
                            id="fname"
                            name="firstname"
                            placeholder="John M. Doe"
                          />
                          <label htmlFor="email">
                            <i className="fa fa-envelope" /> Email
                          </label>
                          <input
                            type="text"
                            id="email"
                            name="email"
                            placeholder="john@example.com"
                          />
                          <label htmlFor="adr">
                            <i className="fa fa-address-card-o" /> Address
                          </label>
                          <input
                            type="text"
                            id="adr"
                            name="address"
                            placeholder="542 W. 15th Street"
                          />
                          <label htmlFor="city">
                            <i className="fa fa-institution" /> City
                          </label>
                          <input
                            type="text"
                            id="city"
                            name="city"
                            placeholder="New York"
                          />

                          <div className="row">
                            <div className="col-50">
                              <label htmlFor="state">State</label>
                              <input
                                type="text"
                                id="state"
                                name="state"
                                placeholder="NY"
                              />
                            </div>
                            <div className="col-50">
                              <label htmlFor="zip">Zip</label>
                              <input
                                type="text"
                                id="zip"
                                name="zip"
                                placeholder="10001"
                              />
                            </div>
                          </div>
                        </div>

                        <div className="col-50">
                          <h3>Payment</h3>
                          <label htmlFor="fname">Accepted Cards</label>
                          <div className="icon-container">
                            <i
                              className="fa fa-cc-visa"
                              style={{ color: "navy" }}
                            />
                            <i
                              className="fa fa-cc-amex"
                              style={{ color: "blue" }}
                            />
                            <i
                              className="fa fa-cc-mastercard"
                              style={{ color: "red" }}
                            />
                            <i
                              className="fa fa-cc-discover"
                              style={{ color: "orange" }}
                            />
                          </div>
                          <label htmlFor="cname">Name on Card</label>
                          <input
                            type="text"
                            id="cname"
                            name="cardname"
                            placeholder="John More Doe"
                          />
                          <label htmlFor="ccnum">Credit card number</label>
                          <input
                            type="text"
                            id="ccnum"
                            name="cardnumber"
                            placeholder="1111-2222-3333-4444"
                          />
                          <label htmlFor="expmonth">Exp Month</label>
                          <input
                            type="text"
                            id="expmonth"
                            name="expmonth"
                            placeholder="September"
                          />

                          <div className="row">
                            <div className="col-50">
                              <label htmlFor="expyear">Exp Year</label>
                              <input
                                type="text"
                                id="expyear"
                                name="expyear"
                                placeholder="2018"
                              />
                            </div>
                            <div className="col-50">
                              <label htmlFor="cvv">CVV</label>
                              <input
                                type="text"
                                id="cvv"
                                name="cvv"
                                placeholder="352"
                              />
                            </div>
                          </div>
                        </div>
                      </div>

                      <input
                        type="submit"
                        value="Continue to checkout"
                        className="btn"
                      />
                    </form>
                  </div>
                </div>

                <div className="col-25">
                  <div className="container">
                    <h4>
                      Cart
                      <span className="price" style={{ color: "black" }}>
                        <i className="fa fa-shopping-cart" />
                      </span>
                    </h4>
                    <p>
                      <a href="#">Product 1</a>{" "}
                      <span className="price">$15</span>
                    </p>

                    <hr />
                    <p>
                      Total{" "}
                      <span className="price" style={{ color: "black" }}>
                        <b>$30</b>
                      </span>
                    </p>
                  </div>
                </div>
              </div>
              <button onClick={() => setShowForm(false)}></button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
