import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/productDes.css";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/products/${id}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [id]);

  const addToCart = async (product) => {
    const productId = product.id;
    const username = localStorage.getItem("username");
    try {
      const res = await axios.post("http://localhost:3001/cart", {
        product,
        username,
      });
      console.log(res.data.message);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart();
  };

  const handleQuantityChange = (e) => {
    setQuantity(Number(e.target.value));
  };

  const handleBuy = async () => {
    try {
      await axios.post("http://localhost:3001/buy", {
        username: localStorage.getItem("username"),
        product_id: product.id,
        price: product.price,
      });
      alert("Order placed successfully");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="product-page">
      <div className="product-description">
        <div className="product-images">
          <img
            src="https://imgs.search.brave.com/b64ISZzpFb5umEzliLXr-JvnQN_OhHmS-znep9LfhRk/rs:fit:1200:810:1/g:ce/aHR0cHM6Ly93d3cu/YWJ1bmRhbnRoZWFs/dGguaW5mby93cC1j/b250ZW50L3VwbG9h/ZHMvMjAyMS8wMS9P/cmdhbmljLXByb2R1/Y2UuanBn"
            alt="Product 1"
          />
        </div>
        <div className="product-details">
          <h2>{product.Name}</h2>
          <p className="product-price">RS. {product.price}</p>
          <p className="product-description-text">{product.description}</p>
          <form action="#" method="post">
            <label htmlfor="product-quantity">Select Quantity:</label>
            <input
              type="number"
              id="product-quantity"
              name="product-quantity"
              min="1"
              max="10"
              value="{quantity}"
              onChange={() => {
                handleQuantityChange;
              }}
            />
            <button
              onClick={() => {
                handleAddToCart;
              }}
              type="submit"
            >
              Add to Cart
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
