import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "./adminside";
import "../../css/addadmin.css";

const Addadmin = () => {
  const [username, setusername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setphone] = useState("");
  const [confPassword, setConfPassword] = useState("");

  const [registerStatus, setRegisterStatus] = useState("");
  //   const history = useHistory();
  const navigate = useNavigate();

  const handlesubmit = (e) => {
    e.preventDefault();
    try {
      axios
        .post("http://localhost:3001/registeradmin", {
          email: email,
          username: username,
          password: password,
          phone: phone,
          confPassword: confPassword,
        })
        .then((response) => {
          setRegisterStatus(response);
          console.log(response);
          if (response.data.message) {
            setRegisterStatus(response.data.message);
          } else {
            setRegisterStatus(
              "ACCOUNT CREATED SUCCESSFULLY CHECK YOU EMAIL FOR VERIFICATION "
            );
          }
        });
    } catch (error) {
      if (error.response) {
        setRegisterStatus(error.response.data.message);
      }
    }
  };

  return (
    <div className="main-content">
      <Sidebar />
      <h2>Register an Admin</h2>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-5">
            <div className="card">
              <h2 className="card-title text-center">
                Register <a href="http://opensnippets.com">open snippets</a>
              </h2>
              <div className="card-body py-md-4">
                <form _lpchecked="1">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control"
                      id="name"
                      placeholder="Name"
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      placeholder="Email"
                    />
                  </div>

                  <div className="form-group">
                    <input
                      type="password"
                      className="form-control"
                      id="password"
                      placeholder="Password"
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="password"
                      className="form-control"
                      id="confirm-password"
                      placeholder="confirm-password"
                    />
                  </div>
                  <div className="d-flex flex-row align-items-center justify-content-between">
                    <a href="#">Login</a>
                    <button className="btn btn-primary">Create Account</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Addadmin;
