import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/productDes.css";

const ProductDetail = () => {
  let { id } = useParams();
  const [product, setProduct] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:3001/products/${id}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [id]);

  const addToCart = async (product) => {
    const productId = product.id;
    const username = localStorage.getItem("username");
    try {
      const res = await axios.post("http://localhost:3001/cart", {
        product,
        username,
      });
      console.log(res.data.message);
    } catch (error) {
      console.error(error);
    }
  };

  const handleBuy = async () => {
    try {
      await axios.post("http://localhost:3001/buy", {
        username: localStorage.getItem("username"),
        product_id: product.id,
        price: product.price,
      });
      alert("Order placed successfully");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="product-description">
      <div className="product-images">
        <img
          src="https://imgs.search.brave.com/b64ISZzpFb5umEzliLXr-JvnQN_OhHmS-znep9LfhRk/rs:fit:1200:810:1/g:ce/aHR0cHM6Ly93d3cu/YWJ1bmRhbnRoZWFs/dGguaW5mby93cC1j/b250ZW50L3VwbG9h/ZHMvMjAyMS8wMS9P/cmdhbmljLXByb2R1/Y2UuanBn"
          alt="Product 1"
        />
      </div>
      <div className="product-details">
        <h2>Product Name</h2>
        <p className="product-price">$10.00</p>
        <p className="product-description-text">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam vel
          tincidunt quam. Vivamus euismod laoreet massa, a fringilla lorem.
          Quisque semper odio at quam maximus congue. Praesent blandit risus
          eget est aliquet, vitae fringilla nulla imperdiet. Fusce euismod, urna
          ut bibendum convallis, ipsum lorem elementum sapien, vel semper justo
          risus vitae risus.
        </p>
        <form action="#" method="post">
          <label htmlFor="product-size">Select Size:</label>
          <select name="product-size" id="product-size">
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
          <button type="submit">Add to Cart</button>
        </form>
      </div>
    </div>
  );
};

export default ProductDetail;
